import forca0 from '.assets/forca0.png'
import forca1 from '.assets/forca1.png'
import forca2 from '.assets/forca2.png'
import forca3 from '.assets/forca3.png'
import forca4 from '.assets/forca4.png'
import forca5 from '.assets/forca5.png'
import forca6 from '.assets/forca6.png'
import palavras from './palavras'

const alfabeto = ["a", "b", "c", "d", "e", "f", "g", "h", "i", "j", "k", "l", "m", "n", "o", "p", "q", "r", "s", "t", "u", "v", "w", "x", "y", "z"]
const imagens = [forca0, forca1, forca2, forca3, forca4, forca5, forca6]

export default function App() {

  const [desabilitadorElementos, setDesabilitadorElementos] = useState(true);
  const [palavraRandom, setPalavraRandom] = useState('');
  const [palavraJogo, setPalavraJogo] = useState({})
  const [letrasEscolhidas, setLetrasEscolhidas] = useState([...alfabeto])
  const [erros, setErros] = useState(0)
  const [classeCor, setClasseCor] = useState('black')

  function inativar(){
    setDesabilitadorElementos(true);
    setLetrasEscolhidas([...alfabeto])    
  }

  function comecarJogo(){
    setDesabilitadorElementos(false);
    setLetrasEscolhidas([])
    setErros(0)
    setClasseCor('black')    
    const indiceRandom = Math.floor(Math.random() * palavras.length)
    const palavra = palavras[indiceRandom]
    setPalavraRandom(palavra);
    const arrayPalavra = palavra.split('');
    const espacosEmBranco = arrayPalavra.map(letra => ' _')
    setPalavraJogo(espacosEmBranco)
  }

  function selecionarLetra(letra) {
    const novo = [...letrasEscolhidas, letra];
    setLetrasEscolhidas(novo);

    if (palavraRandom.includes(letra)){
      const novaPalavra = [...palavraJogo]
      palavraRandom.forEach ((l,i) => {
        if (l === letra){
          novaPalavra[i] = l
        }
      })
      setPalavraJogo(novaPalavra)
      if (palavraRandom.join('') === novaPalavra.join('')){
        setClasseCor('green')
        inativar()
      }
    } else {
        const totalDeErros = erros + 1
        setErros(totalDeErros)

      if(totalDeErros === 6){
        setPalavraJogo(palavraRandom)
        setClasseCor('red')
        inativar()        
      }
    }

  }

  return (
    <div className='tela-inteira'>
      <div className='forca'>
        <img src={imagens[erros]} alt='img da forca' />
        <button onClick={comecarJogo}>Escolher Palavra</button>
        <h1 className={classeCor}>{palavraJogo}</h1>
      </div>
      <div className='alfabeto'>
        {alfabeto.map(letra=> <button key={letra} disabled={letrasEscolhidas.includes(letra)} onClick={()=>selecionarLetra(letra)}>{letra}</button>)}
      </div>
      <div className='input'>
        <span>JÃ¡ sei a palavra</span>
        <input disabled={desabilitadorElementos}/>
        <button disabled={desabilitadorElementos}>Chutar</button>
        </div>      
    </div>
  );
}
